css:
/* Apply global font settings */
body, .print-format {
    font-family: "Arial", sans-serif !important;
    font-size: 12px !important;
    color: #000;
    padding: 0 !important;
    margin: 0 !important;
}

/* Adjust table and text sizing */
.print-format table,
.print-format th,
.print-format td {
    font-size: 12px !important;
    line-height: 1.4 !important;
}

/* Reduce page margins for print */
@media print {
    @page {
        margin: 10mm !important;
    }

    body {
        margin: 0 !important;
        padding: 0 !important;
    }
}

/* Optional: widen content area */
.print-format {
    width: 100% !important;
    max-width: 210mm;
    margin: 0 auto;
}

/* Apply to all tables inside the print format */
.print-format table,
.print-format th,
.print-format td,
.print-format thead,
.print-format tbody,
.print-format tfoot {
    font-size: 12px !important;
    line-height: 1.3 !important;
    color: #000 !important;
}

/* Also target nested elements */
.print-format table * {
    font-size: 12px !important;
    color: #000 !important;
}

.print-format th {
    font-weight: bold !important;
    font-size: 12px !important;
    background-color: #f5f5f5;
}

.print-format .info-table td {
    padding: 4px 8px;
}

.print-format .signature-section {
    margin-top: 40px;
    page-break-inside: avoid;
}

.print-format .terms-section {
    margin-top: 20px;
    font-size: 11px;
    line-height: 1.5;
}
